package introtojava;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 123;
		int y = 321;
		
		int var1 = (x + y);
		int var2 = (y - x);
		
		System.out.println(var1);
		System.out.println(var2);
		
		var1 = 10;
		var2 = 20;
	
		
	}

}
